mapper.insert:
INSERT INTO user_area (user_sid, tag_id) VALUES (
(SELECT sid FROM User Where User.id = #{id}),
(SELECT tag_id FROM Area_tag WHERE Area_tag.name = #{areaName}));

mapper.delete:
DELETE FROM user_area WHERE (
user_sid = (SELECT sid FROM User Where User.id = #{id}) AND
tag_id = (SELECT tag_id FROM Area_tag WHERE Area_tag.name = #{areaName}));
